SELECT 
    COUNT(*) as total_promoters,
    COUNT(CASE WHEN id_card_url IS NOT NULL THEN 1 END) as with_id_card_url,
    COUNT(CASE WHEN passport_url IS NOT NULL THEN 1 END) as with_passport_url,
    COUNT(CASE WHEN id_card_url IS NOT NULL AND passport_url IS NOT NULL THEN 1 END) as complete_docs,
    COUNT(CASE WHEN id_card_url IS NULL AND passport_url IS NULL THEN 1 END) as no_docs,
    ROUND(100.0 * COUNT(CASE WHEN id_card_url IS NOT NULL AND passport_url IS NOT NULL THEN 1 END) / COUNT(*), 1) || '%' as completion_percentage
FROM promoters;

